import React, { useEffect, useState } from 'react';

function Form(props) {
	let [products, setProducts] = useState();
	let [type, setType] = useState();
	let [name, setName] = useEffect(() => {
		fetch('/product').then((response) =>
			response.json().then((data) => {
				setProducts(data);
			}),
		);
	}, []);

	let showProduct = () => {
		if (products == null) {
			return <div> loading.. </div>;
		}
		let productTypes = products.map((product) => (
			<option value={product._id}>{product.name}</option>
		));
		return productTypes;
	};

	return (
		<div>
			<form>
				<div className='input'>
					<label htmlFor='product-type'>Loại vật tư</label>
					<select name='product-type'>{showProduct()}</select>
				</div>
				<div className='input'>
					<label htmlFor='name'>Tên Kho</label>
					<br />
					<input
						className='product-input'
						type='text'
						name='name'
						placeholder='Name'
						onChange={() => {}}
					/>
				</div>
				<div className='input'>
					<label htmlFor='quantity'>Số lượng</label>
					<br />
					<input
						className='product-input'
						type='text'
						name='quantity'
						placeholder='quantity'
						onChange={() => {}}
					/>
				</div>
				<div className='btn btn-outline-primary' onClick={() => {}}>
					{' '}
					Submit{' '}
				</div>
			</form>
		</div>
	);
}

export default Form;
